import React, { useEffect, useState } from "react";
import { Button, Container, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import {
  deleteSkills,
  getSkills,
} from "../../../../features/skills/skillsSlice";
import CustomPagination from "../../../common/CustomPagination";
import { toast } from "react-toastify";
import AddSkills from "./AddSkills";
import EditSkills from "./EditSkills";
import SkillIconRenderer from "./SkillIconRenderer";

const SkillsMainpage = () => {
  const dispatch = useDispatch();
  const { skills, status, error } = useSelector((state) => state.skills);
  // add skills
  const [showAddSkillsModal, setShowAddSkillsModal] = useState(false)
  const handleSkills = () =>{
    setShowAddSkillsModal(true)
  }
  // edit skills
  const [showEditSkillsModal, setShowEditSkillsModal] =useState(false);
  const [selectedSkillForEdit, setSelectedSkillForEdit] = useState(null);

  const handleEditSkills = (skillId) => {
    const skillToEdit = skills.find(skill => skill._id === skillId);
    if (skillToEdit) {
      setSelectedSkillForEdit(skillToEdit);
      setShowEditSkillsModal(true);
    } else {
      toast.error("Could not find skill data to edit.");
    }
  };
  // pagination
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = skills.slice(indexOfFirstItem, indexOfFirstItem + itemsPerPage); // Corrected slicing for last page
  const totalPages = Math.ceil(skills.length / itemsPerPage);
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };
  // get data
  useEffect(() => {
    if (status === "idle") {
      dispatch(getSkills());
    }
  }, [status, dispatch]);

  useEffect(() => {
    if (error) {
      toast.error(error);
    }
  }, [error]);
  // DeleteHandle
  const handleDelete = (id) => {
    dispatch(deleteSkills(id))
      .unwrap()
      .then(() => {
        toast.success("Delete Succussfully skills");
      })
      .catch((err) => {
        toast.error(`Failed to delete education: ${err.message || err}`);
      });
  };
  return (
    <>
      <Container>
        <div className="d-flex justify-content-between m-5">
          <h2>Skill</h2>
          <Button variant="primary" onClick={handleSkills}>Add to Skills</Button>
        </div>
        {status !== "loading" && currentItems && currentItems.length > 0 ? (
          <div className=" m-4 custom-responsive-table-container mb-4 text-center ">
            <Table striped bordered hover responsive className="table-light">
              <thead>
                <tr>
                  <th>S.no</th>
                  <th>skillName</th>
                  <th>skillIcons</th>
                  <th>skillDescription</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {currentItems.map((skill, i) => (
                  <tr key={skill._id}>
                    <td>{indexOfFirstItem + i + 1}</td>
                    <td>{skill.skillName}</td>
                    <td>
                      {/* CORRECTED ICON RENDERING: */}
                      {/* {skill.skillIcons ? (
                        // Check if skillIcons is a URL (simple check for http/https)
                        skill.skillIcons.startsWith("http") ||
                        skill.skillIcons.startsWith("/") ? (
                          <img
                            src={skill.skillIcons}
                            alt={`${skill.skillName} icon`}
                            style={{ width: "24px", height: "24px" }} // Adjust size as needed
                          />
                        ) : (
                          <i
                            className={skill.skillIcons}
                            style={{ fontSize: "1.5rem" }}
                            title={`${skill.skillName} icon`}
                          ></i>
                        )
                      ) : (
                        // Fallback text if skill.skillIcons is missing or empty
                        <span
                          className="text-muted"
                          style={{ fontStyle: "italic", color: "red" }}
                        >
                          No Icon Available
                        </span>
                      )} */}
                      <SkillIconRenderer 
                      skillIcon={skill.skillIcons}
                      />
                    </td>

                    <td>{skill.skillDescription}</td>

                    <td>
                      <Button variant="outline-primary" size="sm" onClick={() => handleEditSkills(skill._id)}>
                        Edit
                      </Button>{" "}
                      {""}
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() => handleDelete(skill._id)}
                      >
                        Delete
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </div>
        ) : status === "loading" ? (
          <p className="text-center mt-3">Loading Skills details...</p>
        ) : (
          <p className="text-center mt-3">No Skills details available.</p>
        )}
      </Container>
      {totalPages > 1 && (
        <CustomPagination
          currentPage={currentPage}
          totalPages={totalPages}
          onPageChange={handlePageChange}
        />
      )}
      {/* AddSkills */}
      <AddSkills
      show= {showAddSkillsModal}
      onHide={() => setShowAddSkillsModal(false)}
      onSaveSuccess={() => {
        setShowAddSkillsModal(false);
        dispatch(getSkills());
      }}
      />
      <EditSkills 
        show={showEditSkillsModal}
        onHide={() => {
          setShowEditSkillsModal(false);
          setSelectedSkillForEdit(null); // Clear selected skill
        }}
        skillToEdit={selectedSkillForEdit}
        onSaveSuccess={() => {
          setShowEditSkillsModal(false);
          setSelectedSkillForEdit(null); // Clear selected skill
          dispatch(getSkills()); // Refresh list
        }}
      />
    </>
  );
};

export default SkillsMainpage;
